import React from 'react';
import ReactDom from 'react-dom';

function Modal(props){
    if(!props.isOpen){
        return null;
    }
    return(
        ReactDom.createPortal(
            <h1>Hola, estoy afuera</h1>,
            document.getElementById('modal'))
    );
}

export default Modal;
