import React from "react";
import { Link } from "react-router-dom";

import "./styles/Badges.css";

import conflogo from "../images/badge-header.svg";

import BadgesList from "../components/BadgesList";
import Badge from "../components/Badge";

class Badges extends React.Component {
  state = {
    data: [
      {
        id: "2de30c42-9deb-40fc-a41f-05e62b5939a7",
        firstName: "Nezuko",
        lastName: "Kamado",
        email: "nezuko@gmail.com",
        jobTitle: "Sister",
        twitter: "nezuko-7573",
        avatarUrl:
          "https://laverdadnoticias.com/__export/1582150922518/sites/laverdad/img/2020/02/19/kimetsu_no_yaiba_nezuko_cosplay.png_423682103.png",
      },
      {
        id: "d00d3614-101a-44ca-b6c2-0be075aeed3d",
        firstName: "Tomioka",
        lastName: "Giyu",
        email: "tomioka@hotmail.com",
        jobTitle: "Master",
        twitter: "tomioka",
        avatarUrl:
          "https://avatarfiles.alphacoders.com/202/202402.png",
      },
      {
        id: "63c03386-33a2-4512-9ac1-354ad7bec5e9",
        firstName: "Kibutsuji",
        lastName: "Muzan",
        email: "muzan@hotmail.com",
        jobTitle: "Enemy",
        twitter: "muzan",
        avatarUrl:
          "https://images.alphacoders.com/106/thumb-350-1068439.png",
      },
    ],
  };

  //constructor(){}
  //componentDidMount(){}
  render() {
    return (
      <React.Fragment>
 
        <div className="Badges">
          <div className="BadgeNew__hero">
            <img className="img-fluid" width="150" src={conflogo} alt="Logo" />
          </div>
        </div>

        <div className="Badge__container">
          <div className="Badges__buttons">
            <Link to="/badges/new" className="btn  btn-primary">
              {" "}
              New Badge{" "}
            </Link>
          </div>
        </div>

        <div className="Badges__list">
          <div className="Badge__container">
            <BadgesList badges={this.state.data} />
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Badges;
