import React from 'react';

import './styles/BadgeEdit.css';
import header from '../images/badge-header.svg';
import Badge from '../components/Badge';
import BadgeForm from '../components/BadgeForm';
import api from '../api';
import PageLoading from '../components/PageLoading';

class BadgeEdit extends React.Component {
  state = {
    loading:true,
    error: null,
    form: {
      firstName: '',
      lastName: '',
      email: '',
      jobTitle: '',
      twitter: '',
    },
  };

  componentDidMount(){
    this.fetchData();
  }
  fetchData= async e=>{
    this.setState({loading: true, error: null})

    try{
      const data = await api.badges.read(
        this.props.match.params.badgeId
      )

      this.setState({loading: false, form:data})
    }catch(error){
      this.setState({loading: false, error:error})
    }
  }

  handleChange = e => {
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      },
    });
  };

  handleSubmit = async e => {
    e.preventDefault();
    this.setState({loading: true,error: null})
    try{
      await api.badges.update( this.props.match.params.badgeId,this.state.form)
      this.setState({loading: false})

      this.props.history.push('/badges');
    }catch(error){
      this.setState({loading: false,error: error})
    }
  };

  render() {
    if(this.state.loading){
      return <PageLoading />
    }
    return (
      <React.Fragment>
  
        <div className="BadgeEdit__hero">
          <img className="img-fluid"  width="150" src={header} alt="Logo" />
        </div>

        <div className="container">
          <div className="row">
            <div className="col-6">
              <Badge
                firstName={this.state.form.firstName || 'PRIMER NOMBRE'}
                lastName={this.state.form.lastName || 'APELLIDO'}
                twitter={this.state.form.twitter || 'USUARIO TWITTER'}
                jobTitle={this.state.form.jobTitle || 'TITULO'}
                email={this.state.form.email || 'CORREO'}
                avatarUrl="https://s.gravatar.com/avatar/f3cf0f9a9952d0e264d548676756e229?s=80"
              />
            </div>

            <div className="col-6">
            <h1>Edit User</h1>
              <BadgeForm
                onChange={this.handleChange}
                onSubmit={this.handleSubmit}
                formValues={this.state.form}
                error={this.state.error}
              />
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default BadgeEdit;
